        rad: r !important;
        c: fg bd bg;
        box-shadow: bsh;
      }
    }
    &-result[href] {
      border-color: bd !important;
      &:hover { c: fg bd bd }
      .st-ui-type-heading {
        c: ac;
        em {
          c: white 0 ac;
          box-shadow: inset 0 -1px ac;
        }
      }
      .st-ui-type-detail, .st-ui-type-detail-bold {
        c: cm;
        em {
          c: ac 0 t;
          box-shadow: inset 0 -1px ac;
        }
      }
    }
