  }

  // * Feature -> Custom logo.
  if ( dark_logo == 1 ) {
    svg.nav-logo {
      filter: drop-shadow(0 2px 4px sh);
      path:nth-child(1) { v: fg 0 }
      path:nth-child(2), path:nth-child(5), path:nth-child(8) { v: bg 0 }
      path:nth-child(7), path:nth-child(4) { v: bg 0 }
      path:nth-child(3), path:nth-child(6) { v: ac 0 }
    }
  }

  // Applications -> Search.
  .search-container {
    border: 1px solid bd;
    rad: r;
    c: fg 0 bg;
    input { c: fg 0 bg }
    .suggestions {
      c: 0 0 bg;
