  // Scroll.
  .scroll-to-top { c: fg 0 hl }

  // Page: https://about.gitlab.com/events/commit/
  .commit-content {
    #learn, #sponsor { c: 0 0 t }
    .logos-container svg path { v: cm }
    .highlight-background { c: 0 0 hl }
    .dark-tabs {
      a { c: cm t }
      .active a, a:focus, a:hover { c: ac ac }
    }
  }
}

