  }

  // * Right sidebar.
  .doc-nav {
    right: -12px;
    height: calc(100vh + 20px);
    c: cm 0 t;
    // Wrapper.
    #markdown-toc {
      a {
        text-decoration: none;
        c: cm;
        &:hover { c: fg }
        &.active { c: ac }
      }
    }
  }

  // * Search results page.
  // ? https://docs.gitlab.com/search/?q=test&idx=gitlab&p=1
  .instantsearch-input {
    .ais-search-box {
      c: 0 0 t;
      &--input {
        rad: r;
        c: fg bd bg;
        box-shadow: bsh;
        &:focus { c: 0 ac }
      }
      &--magnifier { pointer-events: none }
    }
    .search-results {
      .ais-stats {
        &, .stats {
          opacity: 1;
          c: cm;
        }
      }
      .ais-header {
        c: 0 bd;
        + .ais-body {
          label { c: fg }
          label > span { c: fg 0 bd }
        }
      }
      // Results.
      .hit {
        &-content {
          padding: 8px;
          font-size: 1rem;
          c: 0 t;
          box-shadow: none;
          em { c: ac 0 t }
        }
        &-text { c: cm }
        &-tag {
          rad: 0 0 3px 3px;
          c: fg t t;
        }
      }
      .ais-infinite-hits {
        rad: r;
        c: 0 0 bg;
        box-shadow: inner;
        &__empty { padding: 8px }
        &--item:not(:first-child) { box-shadow: 0 -1px bd }
        &--showmore > button {
          rad: r;
          border: 1px solid;
          c: cm bd bg;
        }
      }
    }
  }

  // * Code blocks.
  pre.highlight {
    rad: r;
    c: 0 bd hl;
    box-shadow: bsh;
    code {
      white-space: pre-wrap;
      word-break: break-all;
      rad: inherit;
      c: 0 t;
    }
    // Copy to clipboard.
    .clip-btn {
      top: 1px; right: 1px;
      rad: 0 r 0 0;
      c: cm 0 hl;
      .fa-clipboard::before { color: cm; }
    }
    // Syntax highlighting.
    code, .na, .c, .c1 { c: cm }
    .n, .p, .nb { c: fg }
    .pi { c: red }
    .na { c: cm }
    .s1, .s2 { c: green }
    .no, .kp, .k { c: violet }
    .nt { c: blue }
    .se, .err { c: orange }
    .o, .s, .ss { c: cyan }
  }

  // Footer -> Sources.
  .source-link {
    c: cm 0 bg;
    box-shadow: 0 -1px bd;
    a[href] { c: ac }
  }
}

